import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';

function NotificationModal({ disabled, ...rest }) {
    const [show, setShow] = useState(false);
  
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>
        <Modal show={show} onHide={handleClose} id="notificationModal">
            <Modal.Header closeButton>
                <Modal.Title>Request notification</Modal.Title>
            </Modal.Header>
            <Modal.Body>You have a new request for one of your locations! 
            </Modal.Body>
            <Modal.Footer>
                <button type="button" class="btn btn-primary mr-1 mt-2" onClick={handleClose} >Check request</button>
                <button type="button" class="btn btn-secondary mr-1 mt-2" onClick={handleClose} >Dismiss</button>
            </Modal.Footer>
        </Modal>
      </>
    );
}

export default NotificationModal;
