import React from 'react';

import { getUser, getToken } from './utils/Common';

function Welcome() {
    const user = getUser();

    if(user.role === "Host") {
        var locations;
        
        const requestOptions = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + getToken()
            }
        };

        fetch('http://localhost:5000/api/locations/getByHost/' + user.id, requestOptions)
        .then(res => res.json())
        .then((data) => {
            locations = data;
            console.log(locations);
        })
        .catch(console.log);

        return (
            <div class="container align-middle">
                <main role="main" class="pb-3">
                    <div class="text-center">
                        <h1 class="display-4">Welcome</h1>
                    </div>
                    <div class="text-center mt-4 mb-4">
                        It looks like you don't have a location registered yet. <br/>
                        Would you like to register a new location now?
                    </div>
                    <div class="text-center">
                        <a href="/newLocation"><button class="btn btn-primary">Create new location</button></a>
                    </div>
                </main>
            </div>
        );
    } else {
        return (
            <div class="container align-middle">
                <main role="main" class="pb-3">
                    <div class="text-center">
                        <h1 class="display-4">Welcome</h1>
                    </div>
                </main>
            </div>
        )
    }
}

export default Welcome;
