import React, { Component } from 'react';
import Contacts from './components/hosts';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {
    hosts: []
  }

  componentDidMount() {
    fetch('http://localhost:5000/hosts')
    .then(res => res.json())
    .then((data) => {
      this.setState({ hosts: data })
    })
    .catch(console.log)
  }

  render() {
    return (
      <hosts hosts={this.state.hosts} />
    )
  }
}

export default App;
