import React, { useState } from 'react';
import axios from 'axios';

import { getId } from '../utils/Common'

function CreateLocation() {
    const guestNo = useFormInput('');
    const address = useFormInput('');
    const description = useFormInput('');

    var friday;

    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleCreate = () => {
        setError(null);
        setLoading(true);

        axios
            .post('http://localhost:5000/api/locations',
                {
                    hostId: getId(),
                    maxNoGuests: guestNo.value,
                    physicalAddress: address,
                    description: description
                })
            .then(response => {
                const rId = response.data.id;
            })
            .catch(error => {
                setLoading(false);
                setError("Something went wrong. Please try again later.");
            });
    }

    return (
    <div class="container align-middle">
        <div class="row justify-content-center align-items-center mb-2">
            <h1>Create new location</h1>
        </div>
        <div class="row justify-content-center align-items-center mb-2">
            <div class = "col-4">
                Maximum number of guests<br />
                <input type="number" class="form-control" {...guestNo} />
            </div>
        </div>
        <div class="row justify-content-center align-items-center mb-2">
            <div class = "col-4">
                Available days<br />
                <div class="form-check form-check-inline">
                    <input class="form-check-input" type="checkbox" value="friday" />
                    <label class="form-check-label" for="inlineCheckbox1">{friday}</label>
                </div>
                <div class="form-check form-check-inline">
                    <input class="form-check-input" type="checkbox" id="inlineCheckboxSaturday" value="option2" />
                    <label class="form-check-label" for="inlineCheckbox2">Saturday</label>
                </div>
                <div class="form-check form-check-inline">
                    <input class="form-check-input" type="checkbox" id="inlineCheckboxSunday" value="option3" />
                    <label class="form-check-label" for="inlineCheckbox3">Sunday</label>
                </div>
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            <div class="col-4">
                Physical address<br />
                <textarea class="form-control mb-2" style={{resize: "none"}} {...address}/>
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            <div class="col-4">
                Location description<br />
                <textarea class="form-control mb-2" rows="5" style={{resize: "none"}} {...description}/>
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            <div class="col-4">
                Location pictures<br />
                <input type="file" class="form-control-file mb-2" />
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
        </div>
        <div class="row justify-content-center align-items-center">
                <div class="col-4">
                <button type="button" class="btn btn-primary mr-1" onClick={handleCreate} disabled={loading} >{loading ? 'Loading...' : 'Create'}</button>
                </div>
            </div>
    </div>
    );
}

const useFormInput = initialValue => {
    const [value, setValue] = useState(initialValue);

    const handleChange = e => {
        setValue(e.target.value);
    }
    return {
        value,
        onChange: handleChange
    }
}

export default CreateLocation;
