import React from 'react';
import { getToken } from './utils/Common';

class EditHost extends React.Component {
    state = {
        host: [],
        token: getToken()
    }

    componentDidMount() {
        var queryString = this.props.location.search;
        let params = new URLSearchParams(queryString);
        let id = params.get("id");
        
        const requestOptions = {
            headers: { 'Authorization': 'Bearer ' + this.state.token }
        };

        fetch('http://localhost:5000/api/hosts/' + id, requestOptions)
        .then(res => res.json())
        .then((data) => {
            this.setState({ host: data });
        })
        .catch(console.log)
    }

    handlePhoneChange(event) {
        this.setState({phoneNo: event.target.value})
    }

    handleBirthdayChange(event) {
        this.setState({bd: event.target.value})
    }

    handleLocationChange(event) {
        this.setState({photoLoc: event.target.value})
    }

    sendData() {

    }

    render() {
        return(
        <div class="container">
            <h1>Edit</h1>
            <h4>Host</h4>
            <div class="row">
                <div class="col-md-4">
                    <form onSubmit={this.sendData} action='/hosts' >
                        <div class="form-group">
                            <label>Date of birth</label>
                            <input class="form-control" type="date" id="dob" value={this.state.date} onChange={this.handleBirthdayChange.bind(this)} />
                        </div>
                        <div class="form-group">
                            <label>Telephone number</label>
                            <input class="form-control" type="tel" id="phone" value={this.state.host.telephoneNumber} onChange={this.handlePhoneChange.bind(this)} />
                        </div>
                        <div class="form-group">
                            <label>Profile picture location</label>
                            <input class="form-control" id="picLoc" value={this.state.host.profilePictureLocation} onChange={this.handleLocationChange.bind(this)} />
                        </div>
                        <div class="form-group">
                            <input type="submit" value="Update" class="btn btn-primary" />
                        </div>
                    </form>
                </div>
            </div>
        </div>
        )
    }
}

export default EditHost;
