import React, { Component } from 'react';
import { getToken } from './utils/Common';
import { loadEdit } from './HostsCommands';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { useState } from 'react';

function Example() {
    const [show, setShow] = useState(false);
  
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
  
    return (
      <>
        <Button variant="primary" onClick={handleShow}>
          Launch demo modal
        </Button>
  
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
}

class Hosts extends Component {
    state = {
        hosts: [],
        token: getToken()
    }

    componentDidMount() {
        const requestOptions = {
            headers: { 'Authorization': 'Bearer ' + this.state.token }
        };

        fetch('http://localhost:5000/api/hosts', requestOptions)
        .then(res => res.json())
        .then((data) => {
            this.setState({ hosts: data })
        })
        .catch(console.log)
    }

    loadDetails(id) {
        window.location.href = "/hosts/detail?id=" + id;
    }

    render() {
        return(
            <div class="container">
                <div class="row">
                    <div class="col">
                        <h1>Hosts</h1>
                    </div>
                </div>
                <div class="row">
                    <div class="col">
                        <a href="/hosts/create">Create new</a>
                    </div>
                </div>
                <br></br>
                {this.state.hosts.map((host) => (
                    <div class = "card mb-4">
                        <div class = "card-body">
                            <h5 class="card-title">{host.id}</h5>
                            <h6 class="card-subtitle mb-2 text-muted">{host.dateOfBirth}</h6>
                            <p class="card-text">{host.telephoneNumber}</p>
                                <button type="button" class="btn btn-primary mr-1" onClick={()=>this.loadDetails(host.id)} >Details</button>
                                <button type="button" class="btn btn-primary mr-1" onClick={()=>loadEdit(host.id)}>Edit</button>
                                <a href="\hosts\detail"><button type="button" class="btn btn-danger mr-1" >Delete</button></a>
                                <Example />
                        </div>
                    </div>
                ))}
                <br></br>
            </div>
        );
    }
}

export default Hosts;
