import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';

import { getToken, getUser, removeUserSession } from '../utils/Common';

function DetailsGuestModal({ guestID: guestid, guestRequestId, ...rest }) {
    const [show, setShow] = useState(false);
  
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const handleAccept = () => {
        const requestOptions = {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + getToken()
              }
            };
        
            fetch('http://localhost:5000/api/GuestRequest/' + guestRequestId, requestOptions)
              .then(response => response.json())
              .then(data => this.setState({ postId: data.id }))
              .catch(err => console.log(err));
            
            handleClose();
    
            if(getUser().role === "Guest") {
              removeUserSession();
              window.location.href = "/welcome";
            } else {
              window.location.href = "/guests";
            }
    };

    const handleReject = () => {
      const requestOptions = {
        method: 'DELETE',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + getToken()
          }
        };
    
        fetch('http://localhost:5000/api/GuestRequest/' + guestRequestId, requestOptions)
          .then(response => response.json())
          .then(data => this.setState({ postId: data.id }))
          .catch(err => console.log(err));
        
        handleClose();

        if(getUser().role === "Guest") {
          removeUserSession();
          window.location.href = "/welcome";
        } else {
          window.location.href = "/guests";
        }
    }
  
    return (
      <>
        <button type="button" class="btn btn-primary mr-2 mt-2" onClick={handleShow} >Details</button>
  
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Guest Details</Modal.Title>
          </Modal.Header>
          <Modal.Body>Guest Details</Modal.Body>
          <Modal.Footer>
            <button type="button" class="btn btn-success mr-1 mt-2" onClick={handleAccept} >Accept</button>
            <button type="button" class="btn btn-danger mr-1 mt-2" onClick={handleReject} >Reject</button>
          </Modal.Footer>
        </Modal>
      </>
    );
}

export default DetailsGuestModal;
