import React, { useState } from 'react';
import axios from 'axios';

import { getToken, getUser } from '../utils/Common'

function CreateLocation() {
    const guestNo = useFormInput('');
    const address = useFormInput('');
    const description = useFormInput('');

    var friday = false;
    var saturday = false;
    var sunday = false;

    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleFriday = (event) => {
        if(friday === true)
            friday = false;
        else
            friday = true;
        
        event.target.checked = friday;
    }
    
    const handleSaturday = (event) => {
        if(saturday === true)
            saturday = false;
        else
            saturday = true;

        event.target.checked = saturday;
    }
    
    const handleSunday = (event) => {
        if(sunday === true)
            sunday = false;
        else
            sunday = true;

        event.target.checked = sunday;
    }

    const handleCreate = () => {
        setError(null);
        setLoading(true);

        axios
            .post('http://localhost:5000/api/locations',
                {
                    hostId: getUser().id,
                    maxNoGuests: parseInt(guestNo.value),
                    physicalAddress: address.value,
                    description: description.value
                },
                { 'headers':
                    { 'Authorization' : 'Bearer ' + getToken() }})
            .then(response => {
                const rId = response.data.id;

                console.log(friday);
                console.log(saturday);
                console.log(sunday);

                if(friday === true) {
                    axios
                        .post("http://localhost:5000/api/locationDates",
                            {
                                locationId: rId,
                                day: "Friday"
                            },
                            { 'headers':
                                { 'Authorization' : 'Bearer ' + getToken() }})
                        .then(response => {
                            if(saturday === true) {
                                axios
                                    .post("http://localhost:5000/api/locationDates",
                                        {
                                            locationId: rId,
                                            day: "Saturday"
                                        },
                                        { 'headers':
                                            { 'Authorization' : 'Bearer ' + getToken() }})
                                    .then(response => {
                                        if(sunday === true) {
                                            axios
                                                .post("http://localhost:5000/api/locationDates",
                                                    {
                                                        locationId: rId,
                                                        day: "Sunday"
                                                    },
                                                    { 'headers':
                                                        { 'Authorization' : 'Bearer ' + getToken() }})
                                                .then(response => {
                                                    setLoading(false);
                                                    //window.location.href='/';
                                                })
                                                .catch(error => {
                                                    setLoading(false);
                                                    setError("Something went wrong. Please try again later.");
                                                })
                                        } else {
                                            setLoading(false);
                                            //window.location.href='/';
                                        }
                                    })
                                    .catch(error => {
                                        setLoading(false);
                                        setError("Something went wrong. Please try again later.");
                                    })
                            } else {
                                if(sunday === true) {
                                    axios
                                        .post("http://localhost:5000/api/locationDates",
                                            {
                                                locationId: rId,
                                                day: "Sunday"
                                            },
                                            { 'headers':
                                                { 'Authorization' : 'Bearer ' + getToken() }})
                                        .then(response => {
                                            setLoading(false);
                                            //window.location.href='/';
                                        })
                                        .catch(error => {
                                            setLoading(false);
                                            setError("Something went wrong. Please try again later.");
                                        })
                                } else {
                                    setLoading(false);
                                    //window.location.href='/';
                                }
                            }
                        })
                        .catch(error => {
                            setLoading(false);
                            setError("Something went wrong. Please try again later.");
                        })
                } else {
                    axios
                        .post("http://localhost:5000/api/locationDates",
                            {
                                locationId: rId,
                                day: "Friday"
                            },
                            { 'headers':
                                { 'Authorization' : 'Bearer ' + getToken() }})
                        .then(response => {
                            if(saturday === true) {
                                axios
                                    .post("http://localhost:5000/api/locationDates",
                                        {
                                            locationId: rId,
                                            day: "Saturday"
                                        },
                                        { 'headers':
                                            { 'Authorization' : 'Bearer ' + getToken() }})
                                    .then(response => {
                                        if(sunday === true) {
                                            axios
                                                .post("http://localhost:5000/api/locationDates",
                                                    {
                                                        locationId: rId,
                                                        day: "Sunday"
                                                    },
                                                    { 'headers':
                                                        { 'Authorization' : 'Bearer ' + getToken() }})
                                                .then(response => {
                                                    setLoading(false);
                                                   // window.location.href='/';
                                                })
                                                .catch(error => {
                                                    setLoading(false);
                                                    setError("Something went wrong. Please try again later.");
                                                })
                                        } else {
                                            setLoading(false);
                                            //window.location.href='/';
                                        }
                                    })
                                    .catch(error => {
                                        setLoading(false);
                                        setError("Something went wrong. Please try again later.");
                                    })
