                                }
                            }
                        })
                        .catch(error => {
                            setLoading(false);
                            setError("Something went wrong. Please try again later.");
                        })
                }

                if(friday === true) {
                    axios
                        .post("http://localhost:5000/api/locationDates",
                            {
                                locationId: rId,
                                day: "Friday"
                            },
                            { 'headers':
                                { 'Authorization' : 'Bearer ' + getToken() }})
                        .catch(error => {
                            setLoading(false);
                            setError("Something went wrong. Please try again later.");
                        })
                }

                if(saturday === true) {
                    axios
                        .post("http://localhost:5000/api/locationDates",
                            {
                                locationId: rId,
                                day: "Saturday"
                            },
                            { 'headers':
                                { 'Authorization' : 'Bearer ' + getToken() }})
                        .catch(error => {
                            setLoading(false);
                            setError("Something went wrong. Please try again later.");
                        })
                }

                if(sunday === true) {
                    axios
                        .post("http://localhost:5000/api/locationDates",
                            {
                                locationId: rId,
                                day: "Sunday"
                            },
                            { 'headers':
                                { 'Authorization' : 'Bearer ' + getToken() }})
                        .then(response => {
                            setLoading(false);
                            //window.location.href='/';
                        })
                        .catch(error => {
                            setLoading(false);
