        </div>
        <div class="row justify-content-center align-items-center mb-2">
            <div class = "col-4">
                Maximum number of guests<br />
                <input type="number" class="form-control" {...guestNo} />
            </div>
        </div>
        <div class="row justify-content-center align-items-center mb-2">
            <div class = "col-4">
                Available days<br />
                <div class="form-check form-check-inline">
                    <input id="friday" name="friday" type="checkbox" onClick={handleFriday} />
                    <label class="form-check-label" for="friday">Friday</label>
                </div>
                <div class="form-check form-check-inline">
                    <input id="saturday" name="saturday" type="checkbox" onClick={handleSaturday} />
                    <label class="form-check-label" for="saturday">Saturday</label>
                </div>
                <div class="form-check form-check-inline">
                    <input id="sunday" name="sunday" type="checkbox" onClick={handleSunday} />
                    <label class="form-check-label" for="sunday">Sunday</label>
                </div>
                <div class="form-check form-check-inline">
                    <input id="wtf" name="wut" type="checkbox" />
                    <label class="form-check-label" for="sunday">What</label>
                </div>
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            <div class="col-4">
                Physical address<br />
                <textarea class="form-control mb-2" style={{resize: "none"}} {...address}/>
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            <div class="col-4">
                Location description<br />
                <textarea class="form-control mb-2" rows="5" style={{resize: "none"}} {...description}/>
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            <div class="col-4">
                Location pictures<br />
                <input type="file" class="form-control-file mb-2" />
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
        </div>
        <div class="row justify-content-center align-items-center">
                <div class="col-4">
                <button type="button" class="btn btn-primary mr-1" onClick={handleCreate} disabled={loading} >{loading ? 'Loading...' : 'Create'}</button>
                </div>
            </div>
    </div>
    );
}

const useFormInput = initialValue => {
    const [value, setValue] = useState(initialValue);

    const handleChange = e => {
        setValue(e.target.value);
    }
    return {
        value,
        onChange: handleChange
    }
}

export default CreateLocation;
