import React from 'react';

function CreateLocation() {
    const handleCreate = () => {
        setError(null);
        setLoading(true);

        axios
            .post('http://localhost:5000/api/Hosts',
                {
                    dateOfBirth: dob.value + "T00:00:00",
                    telephoneNumber: phone.value,
                    profilePictureLocation: profilePic.value
                })
            .then(response => {
                const cId = response.data.id;

                axios
                    .post('http://localhost:5000/api/users/registerHost',
                    {
                        firstName: firstName.value,
                        lastName: lastName.value,
                        email: email.value,
                        username: username.value,
                        password: password.value,
                        correspondingID: cId
                    })
                    .then(response => {
                        setLoading(false);
                        window.location.href='/newLocation';
                    })
                    .catch(error => {
                        console.log(cId);
                        setLoading(false);
                        setError("Something went wrong. Please try again later.");
                    });
            })
            .catch(error => {
                setLoading(false);
                setError("Something went wrong. Please try again later.");
            });
    }

    return (
    <div class="container align-middle">
        <div class="row justify-content-center align-items-center mb-2">
            <h1>Create new location</h1>
        </div>
        <div class="row justify-content-center align-items-center mb-2">
            <div class = "col-2">
                Maximum number of guests<br />
                <input type="number" class="form-control" />
            </div>
            <div class = "col-2">
                Maximum number of guests<br />
                <input type="number" class="form-control" />
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            <div class="col-4">
                Physical address<br />
                <textarea class="form-control mb-2" style={{resize: "none"}}/>
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            <div class="col-4">
                Location description<br />
                <textarea class="form-control mb-2" rows="5" style={{resize: "none"}}/>
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
            <div class="col-4">
                Location pictures<br />
                <input type="file" class="form-control-file mb-4" />
            </div>
        </div>
        <div class="row justify-content-center align-items-center">
                <div class="col-4">
                    <button type="button" class="btn btn-primary mr-1" >Create</button>
                </div>
            </div>
    </div>
    );
}

export default CreateLocation;
