import React from 'react';

import { getToken, getUser } from '../utils/Common';
import DeleteHostModal from './DeleteHostModal';

class EditHost extends React.Component {
    state = {
        host: [],
        token: getToken(),
        phoneNo: '',
        bd: '',
        photoLoc: '',
    }

    componentDidMount() {
        var queryString = this.props.location.search;
        let params = new URLSearchParams(queryString);
        let id = params.get("id");
        
        const requestOptions = {
            headers: { 'Authorization': 'Bearer ' + this.state.token }
        };

        fetch('http://localhost:5000/api/hosts/' + id, requestOptions)
        .then(res => res.json())
        .then((data) => {
            var date = data.dateOfBirth.substring(0, 10)
            this.setState({ host: data, phoneNo: data.telephoneNumber, bd: date, photoLoc: data.profilePictureLocation });
        })
        .catch(console.log)
    }

    handlePhoneChange(event) { this.setState({phoneNo: event.target.value}) }
    handleBirthdayChange(event) { this.setState({bd: event.target.value}) }
    handleLocationChange(event) { this.setState({photoLoc: event.target.value}) }

    updateData() {
        var dob = document.getElementById("dob").value;
        var phone = document.getElementById("phone").value;
        var picLoc = document.getElementById("picLoc").value;

        const requestOptions = {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + this.state.token
            },
            body: JSON.stringify({
                "dateOfBirth": String(dob) + "T00:00:00",
                "telephoneNumber": phone,
                "profilePictureLocation": picLoc
            })
        };
        
        fetch('http://localhost:5000/api/Hosts/' + this.state.host.id, requestOptions)
            .then(response => response.json())
            .then(data => this.setState({ postId: data.id }))
            .catch(err => console.log(err));
    }

    render() {
        return(
        <div class="container">
            <h1>Edit</h1>
            <h4>Host</h4>
            <div class="container align-middle">
                <div class="row justify-content-center align-items-center mb-4">
                    <h1>Create Host Account</h1>
                </div>
                <div class="row justify-content-center align-items-center mb-2">
                    <div class = "col-4">
                        Username<br />
                        <input type="text" class="form-control" />
                    </div>
                </div>
                <div class="row justify-content-center align-items-center">
                    <div class="col-4">
                        Password<br />
                        <input type="password" class="form-control mb-4" />
                    </div>
                </div>
                <div class="row justify-content-center align-items-center">
                    <div class = "col-2">
                        First name<br />
                        <input type="text" class="form-control mb-2" />
                    </div>
                    <div class = "col-2">
                        Last name<br />
                        <input type="text" class="form-control mb-2" />
                    </div>
                </div>
                <div class="row justify-content-center align-items-center">
                    <div class="col-4">
                        Email<br />
                        <input type="text" class="form-control mb-2" />
                    </div>
                </div>
                <div class="row justify-content-center align-items-center">
                    <div class="col-4">
                        Telephone number<br />
                        <input type="text" class="form-control mb-2" />
                    </div>
                </div>
                <div class="row justify-content-center align-items-center">
                    <div class="col-4">
                        Date of birth<br />
                        <input type="date" class="form-control mb-2" />
                    </div>
                </div>
                <div class="row justify-content-center align-items-center">
                    <div class="col-4">
                        Profile picture<br />
                        <input type="file" class="form-control-file mb-2" />
                    </div>
                </div>
                <div class="row justify-content-center align-items-center">
                    <div class="col-4">
                        <button type="button" class="btn btn-primary mr-1" >Update</button>
                    </div>
                </div>
            </div>
        </div>
        )
    }
}

export default EditHost;
