import React, { Component } from 'react';

class Hosts extends Component {
    state = {
        hosts: []
    }

    componentDidMount() {
        fetch('http://localhost:5000/api/hosts')
        .then(res => res.json())
        .then((data) => {
            this.setState({ hosts: data })
        })
        .catch(console.log)
    }

    render() {
        return(
            <div class="container">
                <div class="row">
                    <div class="col">
                        <h1>Hosts</h1>
                    </div>
                </div>
                <div class="row">
                    <div class="col">
                        Create new
                    </div>
                </div>
                <br></br>
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Date of Birth</th>
                            <th>Telephone Number</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.hosts.map((host) => (
                            <tr>
                                <td>{host.id}</td>
                                <td>{host.dateOfBirth}</td>
                                <td>{host.telephoneNumber}</td>                            
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default Hosts;
